-- Table structure for op_reconcile_statistics_hour
-- ----------------------------
DROP TABLE IF EXISTS `op_reconcile_statistics_hour`;
CREATE TABLE `op_reconcile_statistics_hour`  (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `data_item_id` int(11) NOT NULL COMMENT '数据项id',
  `reconcile_date` date NULL DEFAULT NULL COMMENT '统计日期',
  `collect_num` int(11) NULL DEFAULT NULL COMMENT '采集数量',
  `receive_num` int(11) NULL DEFAULT NULL COMMENT '入库数量',
  `create_date` timestamp NULL DEFAULT NULL COMMENT '创建时间',
  `correct_num` int(11) NULL DEFAULT NULL COMMENT '更正量',
  `type` char(2) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '类型（01=数据库，02=文件数据,  03=KAFKA,  04=视频,  05=音频，06=图片， 07=文本，08=服务接口）',
  `update_time` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  `hour_period` int(3) NULL DEFAULT NULL COMMENT '小时段',
  `project_id` int(11) NULL DEFAULT NULL COMMENT '项目id',
  `department_id` int(11) NULL DEFAULT NULL COMMENT '部门id',
  `statistics_receive_last_time` datetime NULL DEFAULT NULL COMMENT '上一次接收统计日期',
  `statistics_collect_last_time` datetime NULL DEFAULT NULL COMMENT '上一次采集统计日期',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 299 CHARACTER SET = utf8 COLLATE = utf8_general_ci COMMENT = '每小时数据对账统计' ROW_FORMAT = COMPACT;

