CREATE FUNCTION `queryParentCatalogids`(cid VARCHAR(50))
 RETURNS varchar(4000) CHARSET utf8
BEGIN
	DECLARE sTemp VARCHAR(16383);
	DECLARE sTempChd VARCHAR(16383);
	DECLARE sTempCid VARCHAR(50);
	
	SET sTemp = '$';
	SET sTempChd = cid;
	SET sTempCid = NULL;
	
	WHILE sTempChd IS NOT NULL AND sTempChd <> '0' DO
		SET sTemp = CONCAT(sTemp,',',sTempChd);
		SELECT parent_id INTO sTempCid FROM SYS_CATALOG WHERE id=sTempChd;
		SET sTempChd = sTempCid;
		set sTempCid = null;
	END WHILE;
	
	RETURN sTemp;
END
;;
delimiter ;

SET FOREIGN_KEY_CHECKS = 1;
